mapper.select:
select * from app_file_upload where removed=0 and enabled=1

    and processTypeId = #{processTypeId}


    and processHeaderId = #{processHeaderId}


    and receiptCode = #{receiptCode}


    and receiptId = #{receiptId}


    and fileClass = #{fileClass}


    and finalName = #{finalName}


    and orgId = #{orgId}


    and filePath = #{filePath}


	and createUserId = #{createUserId}


	and attr1 = #{attr1}


    and attr2 = #{attr2}

mapper.delete:
        UPDATE app_file_upload SET removed=1,enabled=0


	            and id = #{id}


	            and finalName = #{finalName}


	            and filePath = #{filePath}


	            and processTypeId = #{processTypeId}


	            and processHeaderId = #{processHeaderId}


	            and receiptCode = #{receiptCode}


	            and receiptId = #{receiptId}


	            and fileClass = #{fileClass}


	            and orgId = #{orgId}


	        	and createUserId = #{createUserId}

mapper.update:
update app_file_upload
description=#{description}, processTypeId =#{processTypeId}, processHeaderId=#{processHeaderId}, receiptCode =#{receiptCode}, receiptId=#{receiptId}, fileClass=#{fileClass}, finalName=#{finalName}, filePath =#{filePath}, fileSize =#{fileSize}, orgId =#{orgId}, attr1 =#{attr1}, attr2 =#{attr2}, attr3 =#{attr3}, enabled =#{enabled}, removed =#{removed}, createUserId=#{createUserId}, createUserCode =#{createUserCode}, createUserName=#{createUserName}, createDate =#{createDate}, updateUserId =#{updateUserId}, updateUserCode=#{updateUserCode}, updateUserName=#{updateUserName}, updateDate =#{updateDate },
where id=#{id}
